import{_ as e,c as o,o as a,j as t,a as n}from"./chunks/framework.CfbOou38.js";const k=JSON.parse('{"title":"简要介绍","description":"","frontmatter":{},"headers":[],"relativePath":"xuyan/intro.md","filePath":"xuyan/intro.md"}'),s={name:"xuyan/intro.md"},r=t("h1",{id:"简要介绍",tabindex:"-1"},[n("简要介绍 "),t("a",{class:"header-anchor",href:"#简要介绍","aria-label":'Permalink to "简要介绍"'},"​")],-1),c=t("p",null,"2024年暑期，我在得知被NTU录取之后，便着手准备迈入人生的一个新篇章。在网上检索有关NTU的信息时，我意外发现了「飞跃手册」这种特殊的wiki。",-1),i=t("p",null,"当时搜索到了复旦大学的生存手册、中科大的飞跃手册、南科大的飞跃手册和上交大的生存指南，在粗略读完上述四本手册之后，我发现这种高密度的高校信息汇总册简直是太方便了，在一个站点就可以实现校园信息的全检索，节省了很多搜索信息的时间，而且最重要的是很大程度上杜绝了虚假信息、无效信息的介入。但是，当我希望能再找到NTU的相关手册时，我发现全网暂时都缺乏这样的内容。",-1),l=t("blockquote",null,[t("p",null,"为什么NTU不能拥有这样的宝藏手册？")],-1),_=t("p",null,"于是，我便诞生出了一个念头：既然目前为止仍然没有NTU手册，那么就让我来做第一人。",-1),d=t("p",null,"在和几位学姐学长的长谈之后，NTU Manual项目便正式确立了。",-1),p=[r,c,i,l,_,d];function u(h,m,f,N,x,T){return a(),o("div",null,p)}const $=e(s,[["render",u]]);export{k as __pageData,$ as default};
